import React from 'react';
import './AddItem.css';

export default class AddItem extends React.Component
{
	state = { label: ''};

	onLabelChange = (e) =>
	{
		this.setState(
		{
			label: e.target.value
		});
		//console.log(e.target.value);
	};

	render()
	{
		const {onAdded} = this.props;
		return(
			<form className="additem d-flex">
				<input type="text" className="form-control" onChange={this.onLabelChange} placeholder="Что добавить"/>
				<button className="btn btn-outline-secondary" onClick={() => onAdded('new label')}>
					Add Item
				</button>
			</form>
		);
	};
}
