import React from 'react';
import AppHeader from '../AppHeader';
import SearchPanel from '../SearchPanel';
import TodoList from '../TodoList';
import ItemStatusFilter from '../ItemStatusFilter';
import './App.css';

export default class App extends React.Component
{
	state = {
		todoData: [
			{label: 'Дело №1', important: false, id: 1},
			{label: 'Дело №2', important: true, id: 2},
			{label: 'Дело №3', important: false, id: 3},
			{label: 'Дело №4', important: true, id: 4},
		]
	};

	deleteItem = (id) =>
	{
		this.setState((state) =>
		{
			const idx = state.todoData.findIndex((el) => el.id === id);
			state.todoData.splice(idx, 1);

			const before = state.todoData.slice(0, idx);
			const after = state.todoData.slice(idx + 1);
			const newArray = [...before, ...after];
			return {
				todoData: newArray
			};
		});
		//console.log(id);	
	};

	render()
	{
		return(
			<div className="todo-app">
				<AppHeader todo={1} done={3}/>
				<div className="top-panel d-flex">
					<SearchPanel/>
					<ItemStatusFilter/>
				</div>
				<TodoList data={this.state.todoData} onDeleted={this.deleteItem}/>
			</div>
		);
	};
}
