import React from 'react';
import AppHeader from '../AppHeader';
import SearchPanel from '../SearchPanel';
import TodoList from '../TodoList';
import ItemStatusFilter from '../ItemStatusFilter';
import AddItem from '../AddItem';
import './App.css';

export default class App extends React.Component
{
	maxId = 100;

	state = {
		todoData: [
		{label: 'label', important: false, done: false, id: 1},
		{label: 'label2', important: false, done: false, id: 2},
			createTodoItem('Дело №1'),
			// this.createTodoItem('Дело №2'),
			// this.createTodoItem('Дело №3'),
			// this.createTodoItem('Дело №4'),
			// this.createTodoItem('Дело №5'),
		]
	};

	createTodoItem = (label) =>
	{
		return {
			label: label,
			important: false,
			done: false,
			id: this.maxId++
		};
	};

	deleteItem = (id) =>
	{
		this.setState((state) =>
		{
			const idx = state.todoData.findIndex((el) => el.id === id);
			//state.todoData.splice(idx, 1);
			const before = state.todoData.slice(0, idx);
			const after = state.todoData.slice(idx + 1);
			const newArray = [...before, ...after];
			return {
				todoData: newArray
			};
		});
	};

	addItem = (label) =>
	{
		const newItem = this.createTodoItem(label);
		this.setState((state) =>
		{
			const newArray = [
				...state.todoData,
				newItem
			];
			return {
				todoData: newArray
			};
		});
	};

	onToggleImportant = (id) =>
	{
		console.log('important', id);
	};

	onToggleDone = (id) =>
	{
		console.log('done', id);
	};

	render()
	{
		return(
			<div className="todo-app">
				<AppHeader todo={1} done={3}/>
				<div className="top-panel d-flex">
					<SearchPanel/>
					<ItemStatusFilter/>
				</div>
				<TodoList data={this.state.todoData}
					onDeleted={this.deleteItem}
					onToggleImportant={this.onToggleImportant}
					onToggleDone={this.onToggleDone}
				/>
				<AddItem onAdded={this.addItem}/>
			</div>
		);
	};
}
